INSERT INTO `sys_oper_log` VALUES (244, '新品种', 1, 'com.ruoyi.system.controller.TbNewSpeciesController.add()', 'POST', 1, 'admin', NULL, '/system/species', '127.0.0.1', '内网IP', '{\"content\":\"绿色超级稻是由中国科学院院士、华中农业大学作物遗传改良国家重点实验室主任张启发教授提出的，是指在高产优质的基础上同时兼有多种病虫害的抗性、肥料的高效吸收利用、以及较强的抗旱性或抗逆性等绿色性状的水稻新品种。绿色超级稻”的培育和推广应用，将实现水稻生产中“少打农药、少施化肥、节水抗旱、优质高产”的目标。\",\"createTime\":\"2023-10-05 17:13:57\",\"newsId\":118,\"params\":{},\"title\":\"绿色超级稻\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-05 17:13:56', 156);
INSERT INTO `sys_oper_log` VALUES (245, '新品种', 1, 'com.ruoyi.system.controller.TbNewSpeciesController.add()', 'POST', 1, 'admin', NULL, '/system/species', '127.0.0.1', '内网IP', '{\"content\":\"由江苏里下河地区农业科学研究所小麦研究室主任高德荣科研团队，通过不懈努力，采取分子育种技术，在国内首次成功选育出高抗赤霉病、抗白粉病的“双抗”高产新品种——扬麦33号.扬麦33赤霉病抗性突出，同时兼抗白粉病等，综合性状优良，实现了抗赤霉病与高产协同遗传改良的重大突破，这种具有“双抗”高产小麦新品种，对于解决我国小麦严重病害问题意义重大。\\\"\",\"createTime\":\"2023-10-05 17:14:51\",\"newsId\":119,\"params\":{},\"title\":\"抗赤霉病高产小麦新品种“扬麦33号”\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-05 17:14:50', 81);
INSERT INTO `sys_oper_log` VALUES (246, '新品种', 1, 'com.ruoyi.system.controller.TbNewSpeciesController.add()', 'POST', 1, 'admin', NULL, '/system/species', '127.0.0.1', '内网IP', '{\"content\":\"中棉113于2019年在甘肃审定，2020年引种到新疆示范种植。生育期113-115天，单铃重5.0-5.5克，衣分45%；抗枯萎病、耐黄萎病；纤维长度31.5mm，断裂比强度31.7cN/tex，马克隆值4.0左右，整齐度指数85.1%，纺纱均匀性指数166。该品种成功突破了早熟、高产、优质、高衣分难以协同改良的技术瓶颈，早熟、高品质特性划时代推动风险棉区宜棉化、优棉化，扩大北疆植棉边界至北纬47°，已成为确保北疆地区高品质棉花稳定供应、且超越“澳棉”品质标准的“芯片品种”。\",\"createTime\":\"2023-10-05 17:15:18\",\"newsId\":120,\"params\":{},\"title\":\"棉花品种-中棉113\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-05 17:15:17', 64);
