INSERT INTO `sys_oper_log` VALUES (349, '学农技', 1, 'com.ruoyi.system.controller.TbLearnTechController.add()', 'POST', 1, 'admin', NULL, '/system/tech', '127.0.0.1', '内网IP', '{\"articleId\":123,\"content\":\"瓜果蔬菜施肥需要掌握好底肥、壮棵肥、促花保果肥的使用，以满足整个生长期枝、叶、花、果的生长需要。\",\"createTime\":\"2023-10-06 15:26:33\",\"params\":{},\"title\":\"掌握好施肥的规律和时间\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-06 15:26:29', 69);
INSERT INTO `sys_oper_log` VALUES (350, '学农技', 1, 'com.ruoyi.system.controller.TbLearnTechController.add()', 'POST', 1, 'admin', NULL, '/system/tech', '127.0.0.1', '内网IP', '{\"articleId\":124,\"content\":\"瓜果蔬菜最容易出现的问题就是光长秧子不结果，这时就需要及时进行控旺，可采用掐尖打顶和叶面喷施相结合，对于秧子生长过于茂盛的，要及时掐掉顶尖，阻止秧苗疯长，使营养更多的往花果输送。叶面喷施可以选择0.5％高浓度磷酸二氢钾喷施，同样能起到控旺的作用，对于其他控旺剂要谨慎使用。\",\"createTime\":\"2023-10-06 15:26:51\",\"params\":{},\"title\":\"做好修剪控旺措施\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-06 15:26:47', 51);
INSERT INTO `sys_oper_log` VALUES (351, '学农技', 1, 'com.ruoyi.system.controller.TbLearnTechController.add()', 'POST', 1, 'admin', NULL, '/system/tech', '127.0.0.1', '内网IP', '{\"articleId\":125,\"content\":\"瓜果蔬菜和果树光长叶开花是不够的，保住花朵和果实才是真的，这个在以前已经分享过很多次，这里再详细介绍一下，不管种植什么瓜果作物或是果树，要想让其开花多、结果多，光施肥多是没有用的，必须要补施相对应的养分。这里需要用到的肥料有磷酸二氢钾、水溶硼和芸苔素，其中磷酸二氢钾被称为瓜果作物的能源库，是一种全能型肥料，既能促花膨果，又能保花保果。水溶硼是花朵往果实转化不可缺少的养分，如果缺少了硼，果实就很难坐成，而且还会形成僵果，导致大量掉落。芸苔素是一种绿色无害的植物生长调节剂，能够调节开花和结果期间的生长平衡，促进叶、花、果同时生长。\",\"createTime\":\"2023-10-06 15:27:04\",\"params\":{},\"title\":\"做好保花保果的养分供应\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-06 15:27:01', 57);
INSERT INTO `sys_oper_log` VALUES (352, '学农技', 1, 'com.ruoyi.system.controller.TbLearnTechController.add()', 'POST', 1, 'admin', NULL, '/system/tech', '127.0.0.1', '内网IP', '{\"articleId\":127,\"content\":\"\\t春季土壤解冻后及时浅耕后耙耱，保蓄土壤深层向上移动的水分不致蒸发。同时，还可提高温和土壤通气性能，促进微生物活动，加速土壤硝化作用，对根系生长和养分吸引极为有利。每次降雨后适墒中耕，破除板结，截断毛细管，降低蒸发面积，减少水分散失。\",\"createTime\":\"2023-10-06 15:31:23\",\"params\":{},\"title\":\"耕作保墒\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-06 15:31:19', 54);
INSERT INTO `sys_oper_log` VALUES (353, '学农技', 1, 'com.ruoyi.system.controller.TbLearnTechController.add()', 'POST', 1, 'admin', NULL, '/system/tech', '127.0.0.1', '内网IP', '{\"articleId\":128,\"content\":\"\\t&nbsp;生物覆盖&nbsp;\\t覆盖后蒸发量显著减少，保持土壤水分（土壤含水量常在13％－16％）而且遇特大暴雨也不会发生径流，防止土壤冲刷和泛盐，增加土壤有机质含量，阻止杂草生长。\",\"createTime\":\"2023-10-06 15:31:37\",\"params\":{},\"title\":\"覆盖保墒\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-06 15:31:33', 57);
INSERT INTO `sys_oper_log` VALUES (354, '学农技', 1, 'com.ruoyi.system.controller.TbLearnTechController.add()', 'POST', 1, 'admin', NULL, '/system/tech', '127.0.0.1', '内网IP', '{\"articleId\":129,\"content\":\"\\t积雪可以增加树盘土壤水分，还可保持根系免受冻害，减轻枝条抽条，是干旱区提高土壤含水量的重要措施。冬季下大雪时，将道路田埂、庄基等积雪收集运送至果园，堆在树盘下并顺树行平整压实，然后上面覆盖碎土或秸杆，保证雪水下渗。\",\"createTime\":\"2023-10-06 15:31:58\",\"params\":{},\"title\":\"积雪保墒\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-06 15:31:55', 46);
INSERT INTO `sys_oper_log` VALUES (355, '学农技', 1, 'com.ruoyi.system.controller.TbLearnTechController.add()', 'POST', 1, 'admin', NULL, '/system/tech', '127.0.0.1', '内网IP', '{\"articleId\":130,\"content\":\"\\t喷雾法是指农药用水配成乳浊液或者悬浮液后，用喷雾机将液态农药喷洒成雾状分散体系的施药方法，是农药施用最常用的方法之一。此法适用于乳油、水剂、水乳剂、可湿性粉剂、可溶性粉剂、可溶性液剂、胶悬剂等剂型，可作茎叶处理，也可做土壤处理。喷雾法具有可直接触及防治对象、分布均匀、防治效果好、操作简便等优点，缺点是施药受气候影响较大、药液易飘移流失、对施药人员安全性较差。\",\"createTime\":\"2023-10-06 15:33:27\",\"params\":{},\"title\":\"喷雾法\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-06 15:33:24', 44);
INSERT INTO `sys_oper_log` VALUES (356, '学农技', 1, 'com.ruoyi.system.controller.TbLearnTechController.add()', 'POST', 1, 'admin', NULL, '/system/tech', '127.0.0.1', '内网IP', '{\"articleId\":131,\"content\":\"\\t这种喷雾法雾滴很粗大，大多采用卫士16型背负式手动喷雾器，每亩喷药液达40千克以上（大田作物）或70千克以上（树林或灌木林），农药用水稀释，药液浓度小于1000毫升/立方米，雾滴直径为400～1000微米。\",\"createTime\":\"2023-10-06 15:33:47\",\"params\":{},\"title\":\"大容量喷雾法\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-06 15:33:44', 45);
INSERT INTO `sys_oper_log` VALUES (357, '学农技', 1, 'com.ruoyi.system.controller.TbLearnTechController.add()', 'POST', 1, 'admin', NULL, '/system/tech', '127.0.0.1', '内网IP', '{\"articleId\":132,\"content\":\"\\t又称常量喷雾法,采用卫士16型背负式手动喷雾器，喷头装有孔径为1.3～1.6毫米的喷片，药液以水稀释，浓度在1000毫升/立方米，每亩喷药液13.3～40.0千克,雾滴直径250～400微米。\",\"createTime\":\"2023-10-06 15:33:57\",\"params\":{},\"title\":\"中容量喷雾法\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-06 15:33:53', 49);
INSERT INTO `sys_oper_log` VALUES (358, '学农技', 1, 'com.ruoyi.system.controller.TbLearnTechController.add()', 'POST', 1, 'admin', NULL, '/system/tech', '127.0.0.1', '内网IP', '{\"articleId\":133,\"content\":\"\\t采用卫士16型背负式手动喷雾器，喷头装有孔径为0.7～1.0毫米的喷片，药液以水稀释，浓度在0.8％～3％，每亩喷药液2.5～12.5千克,雾滴直径为150～250微米。\",\"createTime\":\"2023-10-06 15:34:07\",\"params\":{},\"title\":\"低容量喷雾法\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-06 15:34:04', 47);
INSERT INTO `sys_oper_log` VALUES (359, '学农技', 1, 'com.ruoyi.system.controller.TbLearnTechController.add()', 'POST', 1, 'admin', NULL, '/system/tech', '127.0.0.1', '内网IP', '{\"articleId\":134,\"content\":\"\\t又称极微量喷雾法，采用东方红18型机动喷雾机加上超低容量喷头，以油或水为载体，农药浓度达10％～60％，每亩喷雾液0.15～0.5千克，雾滴直径15～75微米。\",\"createTime\":\"2023-10-06 15:34:19\",\"params\":{},\"title\":\"超低容量喷雾法\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-06 15:34:15', 45);
INSERT INTO `sys_oper_log` VALUES (360, '学农技', 1, 'com.ruoyi.system.controller.TbLearnTechController.add()', 'POST', 1, 'admin', NULL, '/system/tech', '127.0.0.1', '内网IP', '{\"articleId\":135,\"content\":\"\\t通过高压静电发生装置使雾滴带电喷施的方法，药滴在叶片表面沉积量显著增加,可将农药有效利用率提高90％。英国生产的基于ULV设计的“electrodyn ULV+”已经广泛在非洲用于棉铃虫的防治。\",\"createTime\":\"2023-10-06 15:34:54\",\"params\":{},\"title\":\"静电喷雾法\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-06 15:34:50', 43);
INSERT INTO `sys_oper_log` VALUES (361, '学农技', 1, 'com.ruoyi.system.controller.TbLearnTechController.add()', 'POST', 1, 'admin', NULL, '/system/tech', '127.0.0.1', '内网IP', '{\"articleId\":136,\"content\":\"\\t对常规喷雾机具重新设计改造，在喷洒部件的相对一侧加装药物回收装置，将喷雾时没有沉积在靶标植物上的药液收集后抽回药液箱，循环利用，可大幅度地提高农药有效利用率，此项技术在欧美国家已经使用。\",\"createTime\":\"2023-10-06 15:35:03\",\"params\":{},\"title\":\"循环喷雾方法\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-06 15:34:59', 48);
INSERT INTO `sys_oper_log` VALUES (362, '学农技', 1, 'com.ruoyi.system.controller.TbLearnTechController.add()', 'POST', 1, 'admin', NULL, '/system/tech', '127.0.0.1', '内网IP', '{\"articleId\":137,\"content\":\"\\t1、抓好乳食，过好初生关。一是要做好接生，防止仔猪被压死、冻死或难产而死，降低仔猪出生死亡率。二是要在出生24小时内剪犬齿和尾巴，防止咬伤母猪乳头、咬尾和互相咬架，影响哺乳和猪的安全。三是要固定乳头，吃好初乳。初乳中蛋白质含量高，维生素丰富，又含有免疫抗体和镁盐等。初乳除将母体的抗体传递给仔猪，增强仔猪抵抗力外，还具有轻泻性，促进胎粪排泄，初乳中的各种营养物质在仔猪的小肠内几乎全被吸收，有利于仔猪生长。初生仔猪开始吃奶时，往往争抢乳头，为了使同窝猪生长均匀和健壮，应在仔猪生后2～3天内由人工扶助采取扶弱抑强的办法，让仔猪吸吮固定的乳头，即尽量把弱小仔猪固定在前几对乳头。四是要抓好防冻、防压、防病。母猪在冬春季节分娩造成仔猪死亡的原因主要是冻死或被母猪压死，因此加强护理，做好保温、防压、十分重要。五是要做好防病。主要是预防仔猪黄、白痢，多见于产后3～5日龄和15～20日龄。\\t2、抓开食、过好补料关。一是补铁、防贫血。仔猪出生时体内贮存的铁约50毫克，每天生长需7毫克，而仔猪每日从母乳中仅能获得1毫克，如果得不到补充，一般10日龄前仔猪就会因缺铁而出现食欲减退，被毛蓬乱，生长停滞和发生白痢甚至发生死亡。一般在仔猪出生后3天内注射补铁剂。喂了防止僵猪和断奶后患水肿病、白肌病。补硒可和补铁同时进行。三是水的补充。仔猪生长迅速，代谢旺盛需要水量较多，因此从3～5日龄起要补充饮水。据试验，用含盐酸0.8%的水给3～20日龄的仔猪饮用(20日龄后改用清水)，有补胃液分泌不全，消化胃蛋白酶的功效，可提高仔猪断奶体重。四是及时补料。一般在7日龄开始补料，方法是在干燥清洁的木板上放少许料，待开始采食后换放料槽中。开始补料时，一定要按“少喂勤添”的原则，每日饲喂4～6次。及时补料的目的是促进仔猪胃肠道发育，解除仔猪牙床发痒，降低断奶后吃料的应激。\\t3、抓旺食，过好断奶关。母猪的泌乳量在分娩后21天达到高峰，此后逐渐下降，而乳猪所需要的营养是不断增加的，21天后母乳无法满足乳猪的营养需要，所以必须尽可能多地让乳猪采食全价配合饲料。乳猪料要求营养高且易消化。\",\"createTime\":\"2023-10-06 15:37:57\",\"params\":{},\"title\":\"哺乳仔猪\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-06 15:37:53', 66);
INSERT INTO `sys_oper_log` VALUES (363, '学农技', 1, 'com.ruoyi.system.controller.TbLearnTechController.add()', 'POST', 1, 'admin', NULL, '/system/tech', '127.0.0.1', '内网IP', '{\"articleId\":138,\"content\":\"\\t鸡舍内部消毒：先用季胺盐类或（和）戊二醛、酸碱类或卤族等消毒液全面喷洒，然后关闭门窗用福尔马林熏蒸消毒。 \\t鸡舍周围环境、鸡场进出口及道路的定期消毒。每2～3周用2%火碱液消毒或撒生石灰1次；场周围及场内污水池、排粪坑、下水道出口，每1～2个月用漂白粉消毒1次。在大门口设消毒池，使用2%火碱或煤酚皂溶液。 \\t带鸡消毒，使用0.3%过氧乙酸、0.1%新洁尔灭、0.1%的次氯酸钠、季胺盐类等对鸡无不良刺激的消毒药，对鸡及舍内环境进行定期消毒。 \",\"createTime\":\"2023-10-06 15:38:26\",\"params\":{},\"title\":\"蛋鸡养殖技术要点\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-06 15:38:22', 47);
INSERT INTO `sys_oper_log` VALUES (364, '学农技', 1, 'com.ruoyi.system.controller.TbLearnTechController.add()', 'POST', 1, 'admin', NULL, '/system/tech', '127.0.0.1', '内网IP', '{\"articleId\":139,\"content\":\"\\t温度：供给适宜的温度，是培育雏鸭最主要的技术措施之一。因为温度适宜，雏鸭热消耗少，用于生长发育的营养相对增加，所以雏鸭的生长发育快，成活率高。采用保温伞保温时，伞可放在房舍的中央或两侧，为了保温，在保温伞的周围围一圈50厘米高的护板或草席，随雏鸭日龄的增长，逐渐扩大范围。直径2米的保温伞可放仔鸭500只左右，2.5米的直径可放750只雏鸭。1日龄保温伞的温度为34-36摄氏度，育雏室的温度为24摄氏度，烟道或煤炉供热。室外温度可升至29-31摄氏度。温度随日龄的增长而逐渐降低，至3周龄时温度降到18-21摄氏度为好。育雏的温度除依据温度计外，也可根据雏鸭的行为动作表现调节温度，从而提高雏鸭的成活率。\",\"createTime\":\"2023-10-06 15:38:46\",\"params\":{},\"title\":\"培育雏鸭需要的环境条件\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-06 15:38:43', 45);
INSERT INTO `sys_oper_log` VALUES (365, '学农技', 1, 'com.ruoyi.system.controller.TbLearnTechController.add()', 'POST', 1, 'admin', NULL, '/system/tech', '127.0.0.1', '内网IP', '{\"articleId\":140,\"content\":\"\\t在许多非牧区的地方，可以看到有些养殖户松散的养殖方式，几口猪，几只羊，而养羊多半简易养殖，平日里用木桩拴上，而羊只就会出现夏天壮，秋天肥。到了冬天，平日里饲喂只能用来抵御寒冷，根本谈不上增膘增重。那么如果想增加养羊效益，就应该改变设施结构，建造布局结构合理的羊舍或暖棚。这样可以开辟饲草资源，使农作物秸秆综合利用，过腹还田，与放牧相比，羊只生长快、出栏早、膘情好、出肉率较高。这样无论在炎夏还是寒冬，羊群都有一个舒适的生活环境，促其快速生长与增重。饲养设施的合理建造与布局、可大大降低疾病的发生，减少在养殖过程中出现疾病等因素带来的损失。 \",\"createTime\":\"2023-10-06 15:39:00\",\"params\":{},\"title\":\"羊养殖技术要点\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-06 15:38:56', 48);
INSERT INTO `sys_oper_log` VALUES (366, '学农技', 1, 'com.ruoyi.system.controller.TbLearnTechController.add()', 'POST', 1, 'admin', NULL, '/system/tech', '127.0.0.1', '内网IP', '{\"articleId\":141,\"content\":\"\\t犊牛持续育肥就是指犊牛断奶后，直接进行育肥（育肥期为10--12个月）。根据肉牛肌肉生长和脂肪沉积的特点，持续育肥牛应分成两个阶段饲养。第一阶段为生长肥育阶段（6--16月龄）;重点是促进牛的骨骼、内脏、肌肉的生长。此时应饲喂富含蛋白质、矿物质和维生素的优质粗料、青贮饲料和糟渣类饲料，日粮中的蛋白质含量应占干物质的13%--14%，尽量多喂粗料，少用精料。第二阶段为成熟育肥阶段，重点是促进肌肉的生长和脂肪的沉积。日粮中粗饲料的比例一般不超过饲料总量的45%，日采食精料量为肉牛体重的2%左右。\",\"createTime\":\"2023-10-06 15:39:13\",\"params\":{},\"title\":\"肉牛养殖技术要点\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-06 15:39:10', 61);
