INSERT INTO `sys_oper_log` VALUES (372, '学农技', 1, 'com.ruoyi.system.controller.TbLearnTechController.add()', 'POST', 1, 'admin', NULL, '/system/tech', '127.0.0.1', '内网IP', '{\"articleId\":147,\"content\":\"\\t农用平地机由机架、牵引（或悬挂）装置、平地铲及其操纵、调节装置和松土铲等组成。具有使平地铲不随地面起伏而上、下运动的反仿形性能，以获得农田平整效果。\",\"createTime\":\"2023-10-06 15:43:46\",\"params\":{},\"title\":\"农用平地机\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-06 15:43:42', 46);
INSERT INTO `sys_oper_log` VALUES (373, '学农技', 1, 'com.ruoyi.system.controller.TbLearnTechController.add()', 'POST', 1, 'admin', NULL, '/system/tech', '127.0.0.1', '内网IP', '{\"articleId\":148,\"content\":\"选择适宜的池塘选址非常重要。考虑土地的坡度、水源供应、排水条件等因素，并根据养鱼规模和种类确定池塘的大小和形状。同时，合理设计池塘的进水口、出水口和过滤系统，以保持水质清洁和循环。\",\"createTime\":\"2023-10-06 15:47:51\",\"params\":{},\"title\":\"池塘选址与设计\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-06 15:47:48', 66);
INSERT INTO `sys_oper_log` VALUES (374, '学农技', 1, 'com.ruoyi.system.controller.TbLearnTechController.add()', 'POST', 1, 'admin', NULL, '/system/tech', '127.0.0.1', '内网IP', '{\"articleId\":149,\"content\":\"维持良好的水质对池塘养鱼至关重要。监测和控制水质参数如pH值、溶解氧、氨氮、硝酸盐等的含量，定期换水并采取必要的处理措施来保持水质稳定。\",\"createTime\":\"2023-10-06 15:48:00\",\"params\":{},\"title\":\"水质管理\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-06 15:47:56', 44);
INSERT INTO `sys_oper_log` VALUES (375, '学农技', 1, 'com.ruoyi.system.controller.TbLearnTechController.add()', 'POST', 1, 'admin', NULL, '/system/tech', '127.0.0.1', '内网IP', '{\"articleId\":150,\"content\":\"根据当地水域条件、市场需求和自身经验，选择适合在池塘中养殖的鱼类。常见的池塘养鱼种类包括鲫鱼、鳜鱼、草鱼等。了解各种鱼类的特点、生长习性、抗病能力等，并根据自身情况进行鱼种的选择。\",\"createTime\":\"2023-10-06 15:48:11\",\"params\":{},\"title\":\"鱼类的选择\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-06 15:48:08', 49);
INSERT INTO `sys_oper_log` VALUES (376, '学农技', 1, 'com.ruoyi.system.controller.TbLearnTechController.add()', 'POST', 1, 'admin', NULL, '/system/tech', '127.0.0.1', '内网IP', '{\"articleId\":151,\"content\":\"定期观察鱼类的行为、食欲和外貌，及时发现异常情况并采取相应措施。合理控制鱼类密度，避免过度捕捞和过度喂食，保持池塘环境的稳定，并注意防止天敌入侵和疾病传播。\",\"createTime\":\"2023-10-06 15:48:20\",\"params\":{},\"title\":\" 养护管理\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-06 15:48:16', 48);
INSERT INTO `sys_oper_log` VALUES (377, '学农技', 1, 'com.ruoyi.system.controller.TbLearnTechController.add()', 'POST', 1, 'admin', NULL, '/system/tech', '127.0.0.1', '内网IP', '{\"articleId\":152,\"content\":\"根据养殖鱼类的营养需求，选择适宜的饲料种类和配方。可以选择商业饲料或自制饲料，确保饲料具有良好的品质和营养成分，满足鱼类的生长需求。\",\"createTime\":\"2023-10-06 15:48:29\",\"params\":{},\"title\":\"饲料种类与配方\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-06 15:48:25', 55);
