INSERT INTO `sys_oper_log` VALUES (489, '问专家', 2, 'com.ruoyi.system.controller.TbAskExpertController.edit()', 'PUT', 1, 'admin', NULL, '/system/expert', '127.0.0.1', '内网IP', '{\"askExpertsId\":272002,\"createBy\":\"\",\"delFlag\":\"0\",\"dictValue\":\"0\",\"expertName\":\"孔令让\",\"introduction\":\"教授，博士生导师，全国杰出专业技术人才，“泰山学者”攀登计划专家。长期从事小麦及其近缘植物重要基因发掘、种质创新和品种选育工作，承担国家自然科学基金重点项目、国家重点研发计划、山东省良种工程等研究课题。先后在SCIENCE、PNAS等期刊发表论文100余篇，出版教材专著2部，获批国家发明专利10项，审定小麦新品种6个并在全国推广种植。特别是在小麦抗赤霉病领域取得的研究成果入选两院院士评选“2020年中国十大科技进展新闻”、“2020年中国生命科学十大进展”、“2021中国农业科学十大重要进展”。2022年8月，“破解小麦癌症的基因密码”被评选为“近十年我国农业科技的30个标志性成就”等。\",\"params\":{},\"remark\":\"http://36.138.166.17:9000/test/species/konglingrang_1696699314901.jpg\",\"updateBy\":\"\",\"updateTime\":\"2023-10-08 01:22:20\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-08 01:22:20', 47);
