INSERT INTO `sys_oper_log` VALUES (492, '问专家', 2, 'com.ruoyi.system.controller.TbAskExpertController.edit()', 'PUT', 1, 'admin', NULL, '/system/expert', '127.0.0.1', '内网IP', '{\"askExpertsId\":2721,\"createBy\":\"\",\"delFlag\":\"0\",\"dictValue\":\"2\",\"expertName\":\"彭福田\",\"introduction\":\"博士，教授，博士生导师，近10年来，在桃现代栽培模式与化肥减施技术研究领域取得了重要成果。先后主持国家自然科学基金2项，国家科技支撑重点项目1项，从2008年起被聘为国家现代桃产业技术体系土肥水管理岗位科学家，2012年带领的果树栽培生理生态与技术研究团队获山东省高校优秀科研创新团队计划支持，2013年参加中组部选派的博士服务团，赴新疆挂职伊犁州林业局副局长，发挥专业优势为新疆民族地区产业振兴贡献了自己的力量，挂职期满考核为优秀等级，被伊犁州政府授予服务伊犁突出贡献奖。\",\"params\":{},\"remark\":\"http://36.138.166.17:9000/test/species/pengfutian_1696699404002.png\",\"updateBy\":\"\",\"updateTime\":\"2023-10-08 01:23:40\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-08 01:23:40', 55);
INSERT INTO `sys_oper_log` VALUES (493, '问专家', 2, 'com.ruoyi.system.controller.TbAskExpertController.edit()', 'PUT', 1, 'admin', NULL, '/system/expert', '127.0.0.1', '内网IP', '{\"askExpertsId\":2718,\"createBy\":\"\",\"delFlag\":\"0\",\"dictValue\":\"1\",\"expertName\":\"桑亚林\",\"introduction\":\"理学博士，教授，林学院院长，博士研究生导师，国家万人计划青年拔尖人才，泰山学者青年专家，山东省学位委员会学科评议组成员，中国林学会林木遗传育种分会理事，林业专业硕士学位点负责人。入选山东农业大学“1512工程”第二层次。长期从事植物干细胞重塑与维持领域的研究工作，在国际上首次提出植物诱导多能干细胞概念。主持国家自然科学基金项目、山东省自然科学基金重大基础研究项目子课题、山东省青创人才团队、山东省农业资源创新利用等项目。\",\"params\":{},\"remark\":\"http://36.138.166.17:9000/test/species/sangyalin_1696699429940.jpg\",\"updateBy\":\"\",\"updateTime\":\"2023-10-08 01:24:11\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-08 01:24:11', 52);
INSERT INTO `sys_oper_log` VALUES (494, '问专家', 2, 'com.ruoyi.system.controller.TbAskExpertController.edit()', 'PUT', 1, 'admin', NULL, '/system/expert', '127.0.0.1', '内网IP', '{\"askExpertsId\":272001,\"createBy\":\"\",\"delFlag\":\"0\",\"dictValue\":\"0\",\"expertName\":\"于振文\",\"introduction\":\"教授，中国工程院院士，博硕导师。兼任农业部小麦专家指导组组长，中国作物学会小麦栽培学组组长，山东省农业专家顾问团小麦分团团长，第九、十、十一届全国政协委员。\",\"params\":{},\"remark\":\"http://36.138.166.17:9000/test/species/yuzhenwen_1696699468500.jpg\",\"updateBy\":\"\",\"updateTime\":\"2023-10-08 01:24:56\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-08 01:24:56', 58);
INSERT INTO `sys_oper_log` VALUES (495, '新品种', 2, 'com.ruoyi.system.controller.TbNewSpeciesController.edit()', 'PUT', 1, 'admin', NULL, '/system/species', '127.0.0.1', '内网IP', '{\"content\":\"绿色超级稻是由中国科学院院士、华中农业大学作物遗传改良国家重点实验室主任张启发教授提出的，是指在高产优质的基础上同时兼有多种病虫害的抗性、肥料的高效吸收利用、以及较强的抗旱性或抗逆性等绿色性状的水稻新品种。绿色超级稻”的培育和推广应用，将实现水稻生产中“少打农药、少施化肥、节水抗旱、优质高产”的目标。\",\"createBy\":\"\",\"createTime\":\"2023-10-05 17:13:57\",\"delFlag\":\"0\",\"newsId\":118,\"params\":{},\"remark\":\"http://36.138.166.17:9000/test/species/19_1696700379272.jpg\",\"title\":\"绿色超级稻\",\"updateBy\":\"\",\"updateTime\":\"2023-10-08 01:40:00\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-08 01:40:00', 82);
INSERT INTO `sys_oper_log` VALUES (496, '新品种', 2, 'com.ruoyi.system.controller.TbNewSpeciesController.edit()', 'PUT', 1, 'admin', NULL, '/system/species', '127.0.0.1', '内网IP', '{\"content\":\"由江苏里下河地区农业科学研究所小麦研究室主任高德荣科研团队，通过不懈努力，采取分子育种技术，在国内首次成功选育出高抗赤霉病、抗白粉病的“双抗”高产新品种——扬麦33号.扬麦33赤霉病抗性突出，同时兼抗白粉病等，综合性状优良，实现了抗赤霉病与高产协同遗传改良的重大突破，这种具有“双抗”高产小麦新品种，对于解决我国小麦严重病害问题意义重大。\\\"\",\"createBy\":\"\",\"createTime\":\"2023-10-05 17:14:52\",\"delFlag\":\"0\",\"newsId\":119,\"params\":{},\"remark\":\"http://36.138.166.17:9000/test/species/20_1696700447623.jpg\",\"title\":\"抗赤霉病高产小麦新品种“扬麦33号”\",\"updateBy\":\"\",\"updateTime\":\"2023-10-08 01:41:01\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-08 01:41:01', 47);
INSERT INTO `sys_oper_log` VALUES (497, '新品种', 2, 'com.ruoyi.system.controller.TbNewSpeciesController.edit()', 'PUT', 1, 'admin', NULL, '/system/species', '127.0.0.1', '内网IP', '{\"content\":\"中棉113于2019年在甘肃审定，2020年引种到新疆示范种植。生育期113-115天，单铃重5.0-5.5克，衣分45%；抗枯萎病、耐黄萎病；纤维长度31.5mm，断裂比强度31.7cN/tex，马克隆值4.0左右，整齐度指数85.1%，纺纱均匀性指数166。该品种成功突破了早熟、高产、优质、高衣分难以协同改良的技术瓶颈，早熟、高品质特性划时代推动风险棉区宜棉化、优棉化，扩大北疆植棉边界至北纬47°，已成为确保北疆地区高品质棉花稳定供应、且超越“澳棉”品质标准的“芯片品种”。\",\"createBy\":\"\",\"createTime\":\"2023-10-05 17:15:18\",\"delFlag\":\"0\",\"newsId\":120,\"params\":{},\"remark\":\"http://36.138.166.17:9000/test/species/21_1696700544104.jpg\",\"title\":\"棉花品种-中棉113\",\"updateBy\":\"\",\"updateTime\":\"2023-10-08 01:42:37\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-08 01:42:37', 48);
INSERT INTO `sys_oper_log` VALUES (498, '新品种', 2, 'com.ruoyi.system.controller.TbNewSpeciesController.edit()', 'PUT', 1, 'admin', NULL, '/system/species', '127.0.0.1', '内网IP', '{\"content\":\"中国农科院选育出了中谷系列谷子、中绿系列绿豆、中红系列红小豆等杂粮新品种，其中“中谷2”米质达到国家一级米标准，成为山东“龙山小米”、新疆“疏勒小米”等品牌小米的主开发品种。\",\"createBy\":\"\",\"createTime\":\"2023-10-05 17:15:49\",\"delFlag\":\"0\",\"newsId\":121,\"params\":{},\"remark\":\"http://36.138.166.17:9000/test/species/22_1696700651773.jpg\",\"title\":\"杂粮新品种\",\"updateBy\":\"\",\"updateTime\":\"2023-10-08 01:44:43\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-08 01:44:43', 48);
