INSERT INTO `sys_oper_log` VALUES (786, '驿站信息', 2, 'com.ruoyi.system.controller.TbStationInfoController.edit()', 'PUT', 1, 'admin', NULL, '/system/info', '127.0.0.1', '内网IP', '{\"content\":\"近日，第20届中国-东盟博览会在广西南宁国际会展中心举行。我市率团参加，以推广优质泰山农产品为重点，携无恙堂、栗欣源、泰山鹿鸣、泰山茶溪谷、东平湖水泊5个知名品牌的核心产品参展。在现场推介环节，市乡村振兴局副局长杨立国以泰山特色为“金字招牌”，系统推介了香醇泰山茶、道地五味泰山药材、优质泰山干鲜果以及独有珍馐泰山水产品，并邀请客户、市民到“好品山东”泰安展区品鉴体验。展品主要涵盖泰山茶、泰山黄精茶、泰山板栗、东平湖水泊系列产品，其中泰山板栗作为“明星产品”，备受青睐。现场观众反应热烈，我市农产品品牌影响力得到极大提高。\",\"createBy\":\"\",\"delFlag\":\"0\",\"newsId\":152,\"params\":{},\"remark\":\"0\",\"title\":\"泰安市农业农村局率团参加第20届中国-东盟博览会\",\"updateBy\":\"\",\"updateTime\":\"2023-10-09 20:36:53\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-09 20:36:52', 56);
