INSERT INTO `sys_oper_log` VALUES (933, '修改其它栏目', 2, 'com.ruoyi.system.controller.TbNewsController.edit()', 'PUT', 1, 'test', NULL, '/system/columns', '127.0.0.1', '内网IP', '{\"content\":\"央广网北京9月24日消息（记者朱敏 李屹 安徽台记者蔡薇）据中央广播电视总台中国之声《新闻和报纸摘要》报道，23日是我国第六个“中国农民丰收节”。各地举办丰富多彩活动庆丰收，神州大地处处洋溢丰收喜悦。五谷丰登，瓜果飘香，稻菽卷起千层浪。放眼这个季节的安徽省芜湖市湾沚区，黄澄澄的稻田、绿油油的菜地、红彤彤的果园…\",\"createBy\":\"\",\"createTime\":\"2023-10-02 21:05:46\",\"delFlag\":\"0\",\"firstColumn\":\"0\",\"newsId\":103,\"params\":{},\"remark\":\"1\",\"secondColumn\":\"1\",\"title\":\"中国农民丰收节丨亿万农民庆丰收、乡村振兴绘新景\",\"updateBy\":\"\",\"updateTime\":\"2023-10-10 21:01:16\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-10 21:01:16', 59);
