INSERT INTO `sys_oper_log` VALUES (942, '线下培训', 2, 'com.ruoyi.system.controller.TbOfflineTrainingController.edit()', 'PUT', 1, 'admin', NULL, '/system/training', '127.0.0.1', '内网IP', '{\"content\":\"2023年7月11日，省派第一书记派驻村村“两委”成员和创业致富带头人培训班在我校开班。副校长林海，山东省委组织部组织三处二级调研员张健，山东省乡村振兴局行业社会组一级调研员张曰林出席开班式。\",\"createBy\":\"\",\"delFlag\":\"0\",\"firstColumn\":\"0\",\"newsId\":151,\"params\":{},\"title\":\"省派第一书记派驻村村“两委”成员和创业致富带头人培训班\",\"updateBy\":\"\",\"updateTime\":\"2023-10-10 21:05:11\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-10 21:05:09', 59);
INSERT INTO `sys_oper_log` VALUES (943, '线下培训', 2, 'com.ruoyi.system.controller.TbOfflineTrainingController.edit()', 'PUT', 1, 'admin', NULL, '/system/training', '127.0.0.1', '内网IP', '{\"content\":\"农稳社稷，粮安天下。近日，由山东省人力资源和社会保障厅主办，我校承办的粮食安全与种质资源保护及粮食精深加工国家级高级研修班正式开班，共有66名专技骨干参加本次研修。本次研修班创新培训模式与方法，采用小组研讨形式，并安排各小组轮流进行每日回顾、开课和结课，既能让大家主动参与研修活动，又能便于学员之间沟通交流。同时，学校严格按照防疫要求，全面做好防疫工作，学员上课全程佩戴口罩，省外的授课老师利用直播软件在线授课，将线上直播与线下面授相结合，通过连麦与学员研讨互动，线下课堂则更加直观地演示教学。\",\"createBy\":\"\",\"delFlag\":\"0\",\"firstColumn\":\"0\",\"newsId\":154,\"params\":{},\"title\":\"粮食安全与种质资源保护及粮食精深加工国家级高级研修班\",\"updateBy\":\"\",\"updateTime\":\"2023-10-10 21:05:16\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-10 21:05:14', 61);
