INSERT INTO `sys_oper_log` VALUES (1002, '驿站信息', 2, 'com.ruoyi.system.controller.TbStationInfoController.edit()', 'PUT', 1, 'admin', NULL, '/system/info', '127.0.0.1', '内网IP', '{\"content\":\"各县（市、区）农业农村局、功能区管委会：省农业农村厅印发了《2023年全省秋冬蔬菜生产管理技术指导意见》，现转发给你们，请认真参照执行，并结合实际切实抓好蔬菜抗涝减灾和秋冬蔬菜稳产保供工作，指导开展秋冬蔬菜生产管理，确保蔬菜市场供应和价格稳定。附件：2023年全省秋冬蔬菜生产管理技术指导意见泰安市农业农村局2023年9月1日\",\"createBy\":\"\",\"delFlag\":\"0\",\"newsId\":1,\"params\":{},\"remark\":\"0\",\"secondColumn\":\"https://img2.baidu.com/it/u=4131401802,1747611691&fm=253&fmt=auto&app=138&f=JPEG?w=889&h=500\",\"title\":\"关于转发《2023年全省秋冬蔬菜生产管理技术指导意见》的通知\",\"updateBy\":\"\",\"updateTime\":\"2023-10-11 10:19:03\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-11 10:19:04', 164);
