INSERT INTO `sys_oper_log` VALUES (1006, '线下培训', 2, 'com.ruoyi.system.controller.TbOfflineTrainingController.edit()', 'PUT', 1, 'admin', NULL, '/system/training', '127.0.0.1', '内网IP', '{\"content\":\"2023年5月25日至31日，发展中国家粮油食品加工技术厂长经理培训班在我校举办，来自巴巴多斯、加纳、尼泊尔、蒙古、巴基斯坦和斯里兰卡的23名农业专员参加培训。山东农业大学党委常委、副校长王勇，中国农业机械化科学研究院集团有限公司国际合作中心主任马腾致辞，山东农业大学国际合作与交流处处长王利、继续教育学院院长张舒参加开班仪式。\",\"createBy\":\"\",\"delFlag\":\"0\",\"firstColumn\":\"0\",\"newsId\":152,\"params\":{},\"remark\":\"http://www.sdau.edu.cn/_upload/article/images/36/b9/530c88124f089a73fb3300af790e/1e9d0be0-02eb-4d72-964d-e33ba5a6d9d1.jpg\",\"title\":\"发展中国家粮油食品加工技术厂长经理培训班在我校举办\",\"updateBy\":\"\",\"updateTime\":\"2023-10-11 10:26:07\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-11 10:26:08', 471);
