INSERT INTO `sys_oper_log` VALUES (1071, '金融', 2, 'com.ruoyi.system.controller.TbFinanceController.edit()', 'PUT', 1, 'admin', NULL, '/system/finance', '127.0.0.1', '内网IP', '{\"content\":\"近年来，建行山东省分行坚持在助力“打造乡村振兴的齐鲁样板”中“走在前”，将服务乡村振兴作为新金融行动新主场，深入推进“1211”乡村振兴综合服务体系。构建“线上+线下”“融资+融智”“金融+政务”的“三农”金融新生态，连续三年在中国人民银行济南分行及中国银行保险监督管理委员会山东监管局金融机构服务乡村振兴考核评估中获得“优秀”档次。\",\"createBy\":\"\",\"delFlag\":\"0\",\"firstColumn\":\"1\",\"newsId\":150,\"params\":{},\"remark\":\"http://36.138.166.17:9000/test/202310111640427354.png\",\"secondColumn\":\"1\",\"title\":\"农行山东省分行提质增效再加力 跑出金融服务乡村振兴“加速度”\",\"updateBy\":\"\",\"updateTime\":\"2023-10-12 11:10:32\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-12 11:10:33', 382);
INSERT INTO `sys_oper_log` VALUES (1072, '新品种', 2, 'com.ruoyi.system.controller.TbNewSpeciesController.edit()', 'PUT', 1, 'admin', NULL, '/system/species', '127.0.0.1', '内网IP', '{\"content\":\"绿色超级稻是由中国科学院院士、华中农业大学作物遗传改良国家重点实验室主任张启发教授提出的，是指在高产优质的基础上同时兼有多种病虫害的抗性、肥料的高效吸收利用、以及较强的抗旱性或抗逆性等绿色性状的水稻新品种。绿色超级稻”的培育和推广应用，将实现水稻生产中“少打农药、少施化肥、节水抗旱、优质高产”的目标。\",\"createBy\":\"\",\"createTime\":\"2023-10-05 17:13:57\",\"delFlag\":\"0\",\"firstColumn\":\"0\",\"newsId\":118,\"params\":{},\"remark\":\"http://36.138.166.17:9000/test/species/19_1696700379272.jpg\",\"title\":\"绿色超级稻\",\"updateBy\":\"\",\"updateTime\":\"2023-10-12 11:14:54\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-12 11:14:55', 396);
INSERT INTO `sys_oper_log` VALUES (1073, '新品种', 2, 'com.ruoyi.system.controller.TbNewSpeciesController.edit()', 'PUT', 1, 'admin', NULL, '/system/species', '127.0.0.1', '内网IP', '{\"content\":\"中棉113于2019年在甘肃审定，2020年引种到新疆示范种植。生育期113-115天，单铃重5.0-5.5克，衣分45%；抗枯萎病、耐黄萎病；纤维长度31.5mm，断裂比强度31.7cN/tex，马克隆值4.0左右，整齐度指数85.1%，纺纱均匀性指数166。该品种成功突破了早熟、高产、优质、高衣分难以协同改良的技术瓶颈，早熟、高品质特性划时代推动风险棉区宜棉化、优棉化，扩大北疆植棉边界至北纬47°，已成为确保北疆地区高品质棉花稳定供应、且超越“澳棉”品质标准的“芯片品种”。\",\"createBy\":\"\",\"createTime\":\"2023-10-05 17:15:18\",\"delFlag\":\"0\",\"firstColumn\":\"0\",\"newsId\":120,\"params\":{},\"remark\":\"http://36.138.166.17:9000/test/species/21_1696700544104.jpg\",\"title\":\"棉花品种-中棉113\",\"updateBy\":\"\",\"updateTime\":\"2023-10-12 11:15:06\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-12 11:15:06', 89);
INSERT INTO `sys_oper_log` VALUES (1074, '问专家', 2, 'com.ruoyi.system.controller.TbAskExpertController.edit()', 'PUT', 1, 'admin', NULL, '/system/expert', '127.0.0.1', '内网IP', '{\"askExpertsId\":2721,\"createBy\":\"\",\"delFlag\":\"0\",\"dictValue\":\"2\",\"direction\":\"果树栽培生理生态与技术\",\"expertName\":\"彭福田\",\"firstColumn\":\"0\",\"introduction\":\"博士，教授，博士生导师，近10年来，在桃现代栽培模式与化肥减施技术研究领域取得了重要成果。先后主持国家自然科学基金2项，国家科技支撑重点项目1项，从2008年起被聘为国家现代桃产业技术体系土肥水管理岗位科学家，2012年带领的果树栽培生理生态与技术研究团队获山东省高校优秀科研创新团队计划支持，2013年参加中组部选派的博士服务团，赴新疆挂职伊犁州林业局副局长，发挥专业优势为新疆民族地区产业振兴贡献了自己的力量，挂职期满考核为优秀等级，被伊犁州政府授予服务伊犁突出贡献奖。\",\"params\":{},\"remark\":\"http://36.138.166.17:9000/test/species/pengfutian_1696699404002.png\",\"researchIntroduction\":\"2016年开始作为山东省农业创新工程项目“主要果树提质增效关键技术研究与示范”首席专家，2017年遴选为山东省高校双一流建设“果树生物学”优势团队带头人，先后编写著作与教材12部，获国家科学技术进步二等奖1项，省部级科技奖励4项，获发明专利2项，实用新型专利2项，选育果树新品种7个，在国内外学术期刊发表论文100多篇。建立了以“肥料袋控缓释，养分稳定供应”为核心的果树节肥与提质增效技术体系，在我国主要果树产区推广近十万亩，节约化肥30%以上，亩节本增效近千元。每年为省地县各级部门举办的技术培训班授课四十余次，培训人员近万人次，为果树产业转型升级做出了贡献。\",\"updateBy\":\"\",\"updateTime\":\"2023-10-12 11:20:51\"}', NULL, 1, '\r\n### Error updating database.  Cause: java.sql.SQLException: Data truncated for column \'user_id\' at row 1\r\n### The error may exist in URL [jar:file:/I:/Workspace/H5C3/SDAU-Village-Station/VillageStationBackend/ruoyi-admin/target/ruoyi-admin.jar!/BOOT-INF/lib/station-3.8.6.jar!/mapper/system/TbAskExpertMapper.xml]\r\n### The error may involve com.ruoyi.system.mapper.TbAskExpertMapper.updateTbAskExpert-Inline\r\n### The error occurred while setting parameters\r\n### SQL: update tb_ask_expert          SET expert_name = ?,             introduction = ?,             dict_value = ?,             del_flag = ?,             create_by = ?,                          update_by = ?,             update_time = ?,             remark = ?,                          direction = ?,             user_id = ?          where ask_experts_id = ?\r\n### Cause: java.sql.SQLException: Data truncated for column \'user_id\' at row 1\n; Data truncat