INSERT INTO `sys_oper_log` VALUES (1076, '修改其它栏目', 2, 'com.ruoyi.system.controller.TbNewsController.edit()', 'PUT', 1, 'admin', NULL, '/system/news', '127.0.0.1', '内网IP', '{\"content\":\"央广网北京9月24日消息（记者朱敏 李屹 安徽台记者蔡薇）据中央广播电视总台中国之声《新闻和报纸摘要》报道，23日是我国第六个“中国农民丰收节”。各地举办丰富多彩活动庆丰收，神州大地处处洋溢丰收喜悦。五谷丰登，瓜果飘香，稻菽卷起千层浪。放眼这个季节的安徽省芜湖市湾沚区，黄澄澄的稻田、绿油油的菜地、红彤彤的果园…\",\"createBy\":\"\",\"createTime\":\"2023-10-02 21:05:46\",\"delFlag\":\"0\",\"firstColumn\":\"0\",\"newsId\":103,\"params\":{},\"secondColumn\":\"1\",\"title\":\"中国农民丰收节丨亿万农民庆丰收、乡村振兴绘新景\",\"updateBy\":\"\",\"updateTime\":\"2023-10-12 11:22:07\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-12 11:22:08', 389);
INSERT INTO `sys_oper_log` VALUES (1077, '线下培训', 2, 'com.ruoyi.system.controller.TbOfflineTrainingController.edit()', 'PUT', 1, 'admin', NULL, '/system/offline_training', '127.0.0.1', '内网IP', '{\"content\":\"乡村振兴，人才为先。8月26日—29日，学校继续教育学院（乡村振兴学院）顺利举办曲阜市优秀年轻村党组织书记乡村振兴能力提升培训班，共有来曲阜市的44名80、90后优秀年轻村党组织书记代表参加本次培训。\",\"createBy\":\"\",\"delFlag\":\"0\",\"firstColumn\":\"0\",\"newsId\":150,\"params\":{},\"remark\":\"https://p2.itc.cn/q_70/images03/20230411/1dd52bdcd9944c6b896479f3f88ec516.png\",\"title\":\"优秀年轻村党组织书记乡村振兴能力提升培训班\",\"updateBy\":\"\",\"updateTime\":\"2023-10-12 11:22:30\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-12 11:22:31', 110);
INSERT INTO `sys_oper_log` VALUES (1078, '推优品', 2, 'com.ruoyi.system.controller.TbRecommendProductsController.edit()', 'PUT', 1, 'admin', NULL, '/system/products', '127.0.0.1', '内网IP', '{\"content\":\"肥城桃，山东省泰安市肥城市特产，全国农产品地理标志.肥城桃简称“肥桃”，因产于山东省肥城市境内而得名。已有1000多年的栽培历史。肥城桃果形端正、美观，呈圆球型，果尖稍凸，缝合线深而明显，梗洼深广，两半对称。成熟后果面底色米黄色(部分阳面有红晕)。果实肥大，粘核，果肉乳白，肉质细嫩致密，柔软多汁，口味甘甜。\",\"createBy\":\"\",\"createTime\":\"2023-10-04 15:11:32\",\"delFlag\":\"0\",\"firstColumn\":\"1\",\"newsId\":112,\"params\":{},\"remark\":\"http://36.138.166.17:9000/test/species/3_1696687869756.jpg\",\"secondColumn\":\"0\",\"title\":\"天上蟠桃，人间肥桃\",\"updateBy\":\"\",\"updateTime\":\"2023-10-12 11:22:42\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-12 11:22:43', 110);
