INSERT INTO `sys_oper_log` VALUES (1113, '问专家', 2, 'com.ruoyi.system.controller.TbAskExpertController.edit()', 'PUT', 1, 'admin', NULL, '/system/expert', '127.0.0.1', '内网IP', '{\"askExpertsId\":1,\"createBy\":\"\",\"delFlag\":\"0\",\"dictValue\":\"2\",\"direction\":\"果树栽培生理研究，果树特色资源分子生物学研究\",\"expertName\":\"束怀瑞\",\"firstColumn\":\"1\",\"introduction\":\"中国工程院院士，博士生导师，中国园艺学会常务理事\",\"params\":{},\"remark\":\"http://36.138.166.17:9000/test/species/shr_1696686150476.png\",\"researchIntroduction\":\"束怀瑞教授从事果树教学科研及推广60年。长期深入生产一线，熟悉我国果树生产和生产技能实际，提出了“沃土养根壮树，提高贮藏营养水平为主的优质丰产技术体系”。从五十年代开始研究苹果根系生物学，发现果树根系具有功能补偿结构特征，提出了“局部效应、界面效应、冗余节能、结构及周年补偿”的观点，开发了多项配套技术，其中发明的“地膜覆盖穴贮肥水技术”，被国家科委列为重点项目在17个省市推广470万亩。“平原地起垅沟草制”、“界面培养基质诱导生根” 等技术对解决果树的逆境早衰等效果极为显著。在国内较早开展果树栽培生理研究，对苹果碳氮营养物质运转、分配、贮藏再利用规律的研究1989年获国家教委科技进步二等奖，提出的“看碳施氮、以氮促碳、养根壮树、优质丰产”的论点对生产起到很大指导作用；提出的苹果三大主枝主干疏层形、枝类组成、枝组概念、优质丰产合理树体结构及调控措施等，成为苹果整形修剪的经典一直沿用至今。在学科建设中注重学科交叉借鉴，将数学、化学等引进果树学，开发果树优质丰产生物数学模型，组织开展果树特色资源分子生物学研究，建立了无融合生殖、矮化砧种子繁殖体系研究。主持的“山东省百万亩苹果幼树优质丰产综合技术研究”，开发108万亩，三年由亩产100公斤/亩提高到1010公斤，取得巨大经济效益，并深入研究根系生物学与根际环境，推广果园覆盖，起垅沟草制，苹果内源激素周年发生规律，农药残毒分析等专题。\",\"updateBy\":\"\",\"updateTime\":\"2023-10-12 17:50:49\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-12 17:50:50', 101);
