INSERT INTO `sys_oper_log` VALUES (1533, '问专家', 2, 'com.ruoyi.system.controller.TbAskExpertController.edit()', 'PUT', 1, 'admin', NULL, '/system/expert', '127.0.0.1', '内网IP', '{\"askExpertsId\":1,\"createBy\":\"管理员\",\"createTime\":\"2023-10-14 16:14:49\",\"delFlag\":\"0\",\"dictValue\":\"2\",\"direction\":\"果树栽培生理研究，果树特色资源分子生物学研究\",\"expertName\":\"束怀瑞\",\"firstColumn\":\"0\",\"introduction\":\"中国工程院院士，博士生导师，中国园艺学会常务理事\",\"params\":{},\"remark\":\"http://36.138.166.17:9000/test/species/shr_1696686150476.png\",\"researchIntroduction\":\"束怀瑞教授从事果树教学科研及推广60年。长期深入生产一线，熟悉我国果树生产和生产技能实际，提出了“沃土养根壮树，提高贮藏营养水平为主的优质丰产技术体系”。从五十年代开始研究苹果根系生物学，发现果树根系具有功能补偿结构特征，提出了“局部效应、界面效应、冗余节能、结构及周年补偿”的观点，开发了多项配套技术，其中发明的“地膜覆盖穴贮肥水技术”，被国家科委列为重点项目在17个省市推广470万亩。“平原地起垅沟草制”、“界面培养基质诱导生根” 等技术对解决果树的逆境早衰等效果极为显著。在国内较早开展果树栽培生理研究，对苹果碳氮营养物质运转、分配、贮藏再利用规律的研究1989年获国家教委科技进步二等奖，提出的“看碳施氮、以氮促碳、养根壮树、优质丰产”的论点对生产起到很大指导作用；提出的苹果三大主枝主干疏层形、枝类组成、枝组概念、优质丰产合理树体结构及调控措施等，成为苹果整形修剪的经典一直沿用至今。在学科建设中注重学科交叉借鉴，将数学、化学等引进果树学，开发果树优质丰产生物数学模型，组织开展果树特色资源分子生物学研究，建立了无融合生殖、矮化砧种子繁殖体系研究。主持的“山东省百万亩苹果幼树优质丰产综合技术研究”，开发108万亩，三年由亩产100公斤/亩提高到1010公斤，取得巨大经济效益，并深入研究根系生物学与根际环境，推广果园覆盖，起垅沟草制，苹果内源激素周年发生规律，农药残毒分析等专题。\",\"updateBy\":\"\",\"updateTime\":\"2023-10-14 20:11:24\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-14 20:11:25', 61);
INSERT INTO `sys_oper_log` VALUES (1534, '问专家', 2, 'com.ruoyi.system.controller.TbAskExpertController.edit()', 'PUT', 1, 'admin', NULL, '/system/expert', '127.0.0.1', '内网IP', '{\"askExpertsId\":2720,\"createBy\":\"管理员\",\"createTime\":\"2023-10-14 16:14:49\",\"delFlag\":\"0\",\"dictValue\":\"2\",\"direction\":\"落叶果树种质资源与遗传育种\",\"expertName\":\"陈学森\",\"firstColumn\":\"0\",\"introduction\":\"果树学二级教授，博士生导师，泰山学者攀登计划及国务院政府特贴专家；曾任国务院学位委员会第六、七届学科评议组园艺学组成员和作物生物学国家重点实验室副主任；现任山东农业大学园艺学科学科负责人，果树学国家重点学科学术带头人，国家苹果工程技术研究中心主任，中国园艺学会李杏分会副理事长，山东省林木良种审定委员会主任委员，《植物遗传资源学报》、《果树学报》及《山东农业大学学报》等编辑委员会委员；山东省农业农村专家顾问团林果分团团长，山东省十强产业智库专家，山东省人大农村与农业委员会顾问。\",\"params\":{},\"remark\":\"http://36.138.166.17:9000/test/species/chenxuesen_1696699276094.jpg\",\"researchIntroduction\":\"针对我国苹果和梨等重要落叶果树野生和地方品种资源挖掘不足缺乏优异种质、缺乏优质高效育种技术育种周期长及苹果和梨主栽品种“晚熟而不优质”等三个突出问题，以新疆重要落叶果树种质资源的挖掘利用为突破口，本团队围绕“优异种质挖掘创制、高效育种技术研发及优质新品种选育”开展研究40余年，实现了苹果和梨优质晚熟耐贮等多性状聚合育种的新突破，为我国果树产业高质量发展做出了重要贡献。\",\"updateBy\":\"\",\"updateTime\":\"2023-10-14 20:11:30\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-14 20:11:30', 56);
INSERT INTO `sys_oper_log` VALUES (1535, '金融', 2, 'com.ruoyi.system.controller.TbFinanceController.edit()', 'PUT', 1, 'admin', NULL, '/system/finance', '127.0.0.1', '内网IP', '{\"collect\":0,\"content\":\"近年来，建设银行青岛市分行不断增强服务乡村振兴战略的能力，把更多的金融资源配置到农村经济社会发展的重点领域和薄弱环节，健全适合农业农村特点的农村金融体系，为乡村振兴战略积极贡献金融新动能。\",\"createBy\":\"\",\"delFlag\":\"0\",\"firstColumn\":\"0\",\"likes\":0,\"newsId\":151,\"params\":{},\"reading\":10,\"remark\":\"http://36.138.166.17:9000/test/202310111641565966.png\",\"secondColumn\":\"1\",\"sort\":\"103\",\"title\":\"乡村振兴丨赋能智慧农业 金融“活水”进村头\",\"updateBy\":\"\",\"updateTime\":\"2023-10-14 20:11:37\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-14 20:11:38', 58);
INSERT INTO `sys_oper_log` VALUES (1536, '线下培训', 2, 'com.ruoyi.system.controller.TbOfflineTrainingController.edit()', 'PUT', 1, 'admin', NULL, '/system/offline_training', '127.0.0.1', '内网IP', '{\"collect\":0,\"content\":\"2023年5月25日至31日，发展中国家粮油食品加工技术厂长经理培训班在我校举办，来自巴巴多斯、加纳、尼泊尔、蒙古、巴基斯坦和斯里兰卡的23名农业专员参加培训。山东农业大学党委常委、副校长王勇，中国农业机械化科学研究院集团有限公司国际合作中心主任马腾致辞，山东农业大学国际合作与交流处处长王利、继续教育学院院长张舒参加开班仪式。\",\"createBy\":\"管理员\",\"createTime\":\"2023-10-14 16:53:02\",\"delFlag\":\"0\",\"firstColumn\":\"0\",\"likes\":0,\"newsId\":152,\"params\":{},\"reading\":3,\"remark\":\"http://36.138.166.17:9000/test/20231012235803152.jpg\",\"sort\":\"103\",\"title\":\"发展中国家粮油食品加工技术厂长经理培训班在我校举办\",\"updateBy\":\"\",\"updateTime\":\"2023-10-14 20:11:45\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-14 20:11:46', 57);
INSERT INTO `sys_oper_log` VALUES (1537, '修改其它栏目', 2, 'com.ruoyi.system.controller.TbNewsController.edit()', 'PUT', 1, 'admin', NULL, '/system/news', '127.0.0.1', '内网IP', '{\"collect\":-3,\"content\":\"解读人 ：泰安市农业农村局党组书记、局长  姜士科\\r\\n\\r\\n【问：文件出台的依据是什么？】\\r\\n\\r\\n答：实施意见的出台主要依据了《山东省第三批“稳中求进”高质量发展政策清单》、《关于加快新型工业化强市建设的实施意见》（泰发〔2022〕5号）、《泰安市现代食品产业链产业图谱暨2022-205年行动计划》（泰工业推进委发〔2022〕14号）这三个文件。\\r\\n\\r\\n【问：请介绍一下文件出台的背景和目的】\\r\\n\\r\\n答：山东省第十二次党代会要求，推进农业全产业链发展，开展品种培优、品质提升、品牌打造行动，明确指出重点支持中央厨房预制菜等现代食品产业发展。今年以来，泰安市委、市政府大力实施新型工业化强市战略，设立了包括现代食品产业链在内的13个重点产业链，构建了“441X”现代产业体系，现代食品被列为全市四大支柱产业之一。同时制定了《关于加快新型工业化强市建设的实施意见》（泰发〔2022〕5号），出台了《现代食品产业链产业图谱暨2022-205年行动计划》，为现代食品产业发展提供了遵循和方向。《实施意见》紧密结合泰安实际，将省市决策部署和规划方案落实落细，明确了具体发展路径、工作措施、保障机制，确保实现总体发展目标，进一步推动泰安现代食品产业更好更快发展，助力新型工业化强市建设。\",\"createBy\":\"管理员\",\"createTime\":\"2023-10-02 22:39:23\",\"delFlag\":\"0\",\"firstColumn\":\"0\",\"likes\":0,\"newsId\":106,\"params\":{},\"reading\":64,\"remark\":\"http://36.138.166.17:9000/test/202310130010495292.png\",\"secondColumn\":\"0\",\"sort\":\"103\",\"title\":\"关于《泰安市人民政府办公室关于加快推进现代食品产业高质量发展的实施意见》的政策解读\",\"updateBy\":\"\",\"updateTime\":\"2023-10-14 20:11:52\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-14 20:11:53', 48);
