INSERT INTO `sys_oper_log` VALUES (1562, '推优品', 2, 'com.ruoyi.system.controller.TbRecommendProductsController.edit()', 'PUT', 1, 'admin', NULL, '/system/products', '127.0.0.1', '内网IP', '{\"collect\":0,\"content\":\"肥城桃，山东省泰安市肥城市特产，全国农产品地理标志.肥城桃简称“肥桃”，因产于山东省肥城市境内而得名。已有1000多年的栽培历史。肥城桃果形端正、美观，呈圆球型，果尖稍凸，缝合线深而明显，梗洼深广，两半对称。成熟后果面底色米黄色(部分阳面有红晕)。果实肥大，粘核，果肉乳白，肉质细嫩致密，柔软多汁，口味甘甜。\",\"createBy\":\"管理员\",\"createTime\":\"2023-10-04 15:11:32\",\"delFlag\":\"0\",\"firstColumn\":\"1\",\"likes\":1,\"newsId\":112,\"params\":{},\"reading\":57,\"remark\":\"http://36.138.166.17:9000/test/species/3_1696687869756.jpg\",\"secondColumn\":\"0\",\"sort\":\"103\",\"title\":\"天上蟠桃，人间肥桃\",\"updateBy\":\"\",\"updateTime\":\"2023-10-14 20:33:36\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-14 20:33:37', 60);
INSERT INTO `sys_oper_log` VALUES (1563, '新品种', 2, 'com.ruoyi.system.controller.TbNewSpeciesController.edit()', 'PUT', 1, 'admin', NULL, '/system/species', '127.0.0.1', '内网IP', '{\"collect\":0,\"content\":\"由江苏里下河地区农业科学研究所小麦研究室主任高德荣科研团队，通过不懈努力，采取分子育种技术，在国内首次成功选育出高抗赤霉病、抗白粉病的“双抗”高产新品种—扬麦33号.扬麦33赤霉病抗性突出，同时兼抗白粉病等，综合性状优良，实现了抗赤霉病与高产协同遗传改良的重大突破，这种具有“双抗”高产小麦新品种，对于解决我国小麦严重病害问题意义重大。\\\"\",\"createBy\":\"管理员\",\"createTime\":\"2023-10-05 17:14:52\",\"delFlag\":\"0\",\"firstColumn\":\"0\",\"likes\":0,\"newsId\":119,\"params\":{},\"reading\":16,\"remark\":\"http://36.138.166.17:9000/test/species/20_1696700447623.jpg\",\"sort\":\"103\",\"title\":\"抗赤霉病高产小麦新品种“扬麦33号”\",\"updateBy\":\"\",\"updateTime\":\"2023-10-14 20:33:43\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-14 20:33:44', 62);
INSERT INTO `sys_oper_log` VALUES (1564, '新品种', 2, 'com.ruoyi.system.controller.TbNewSpeciesController.edit()', 'PUT', 1, 'admin', NULL, '/system/species', '127.0.0.1', '内网IP', '{\"collect\":0,\"content\":\"中国农科院选育出了中谷系列谷子、中绿系列绿豆、中红系列红小豆等杂粮新品种，其中“中谷2”米质达到国家一级米标准，成为山东“龙山小米”、新疆“疏勒小米”等品牌小米的主开发品种。\",\"createBy\":\"管理员\",\"createTime\":\"2023-10-05 17:15:49\",\"delFlag\":\"0\",\"firstColumn\":\"0\",\"likes\":0,\"newsId\":121,\"params\":{},\"reading\":8,\"remark\":\"http://36.138.166.17:9000/test/species/22_1696700651773.jpg\",\"sort\":\"103\",\"title\":\"杂粮新品种\",\"updateBy\":\"\",\"updateTime\":\"2023-10-14 20:33:47\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-14 20:33:48', 59);
INSERT INTO `sys_oper_log` VALUES (1565, '买农资', 2, 'com.ruoyi.system.controller.TbPurchaseFarmController.edit()', 'PUT', 1, 'admin', NULL, '/system/farm', '127.0.0.1', '内网IP', '{\"collect\":0,\"content\":\"即以氮素营养元素为主要成分的化肥，包括碳酸氢铵、尿素、销铵、氨水、氯化铵、硫酸铵等。氮肥是促进花卉根，茎，叶生长的主要肥料，那些不能食用的豆子，花生，瓜子，以及大麻籽，小麻籽等油料作手，都是很好的氮肥原料。若将这些东西发酵腐熟，加水稀释，浇到土壤里，就会促使花卉茁壮成长。\\r\\n\\r\\n \",\"createBy\":\"管理员\",\"createTime\":\"2023-10-14 16:56:58\",\"delFlag\":\"0\",\"firstColumn\":\"1\",\"likes\":0,\"newsId\":1,\"params\":{},\"reading\":54,\"remark\":\"http://36.138.166.17:9000/test/species/df_1696697150830.jpg\",\"secondColumn\":\"0\",\"sort\":\"103\",\"title\":\"氮肥\",\"updateBy\":\"\",\"updateTime\":\"2023-10-14 20:34:01\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-10-14 20:34:02', 60);
