INSERT INTO `tb_finance` VALUES (150, '农行山东省分行提质增效再加力 跑出金融服务乡村振兴“加速度”', '近年来，建行山东省分行坚持在助力“打造乡村振兴的齐鲁样板”中“走在前”，将服务乡村振兴作为新金融行动新主场，深入推进“1211”乡村振兴综合服务体系。构建“线上+线下”“融资+融智”“金融+政务”的“三农”金融新生态，连续三年在中国人民银行济南分行及中国银行保险监督管理委员会山东监管局金融机构服务乡村振兴考核评估中获得“优秀”档次。', '0', '管理员', '2023-10-12 11:10:32', '', '2023-10-12 11:10:32', 'http://202.194.131.202:9000/village-station/202310111640427354.png', '1', '1', '330', 28, 35, 53);
INSERT INTO `tb_finance` VALUES (151, '乡村振兴丨赋能智慧农业 金融“活水”进村头', '近年来，建设银行青岛市分行不断增强服务乡村振兴战略的能力，把更多的金融资源配置到农村经济社会发展的重点领域和薄弱环节，健全适合农业农村特点的农村金融体系，为乡村振兴战略积极贡献金融新动能。', '0', '管理员', '2023-10-12 11:10:32', '', '2023-10-14 20:11:38', 'http://202.194.131.202:9000/village-station/202310111641565966.png', '0', '1', '330', 10, 57, 42);
INSERT INTO `tb_finance` VALUES (152, '山东农担：以金融“活水”润“三农”沃土', '全面推进乡村振兴、实现农业农村现代化，离不开金融工具的支持。山东省农业发展信贷担保有限责任公司（下称“山东农担”）肩负破解“三农”融资难题的历史使命，打造农村金融“新生态”，不断探索数字普惠金融赋能乡村振兴的高质量发展之路，以“活水”润“三农”沃土。', '0', '管理员', '2023-10-12 11:10:32', '', '2023-10-11 11:51:37', 'http://202.194.131.202:9000/village-station/20231011164220506.png', '1', '1', '330', 10, 45, 56);
INSERT INTO `tb_finance` VALUES (155, '金融监管总局：继续引领农村中小银行增加乡村振兴相关领域贷款投放', '金融监管总局表示，将引领农村中小银行深入践行“金融为民”理念，聚焦“三农”领域急难愁盼问题，主动适应农业、新型经营主体和农村产业变化，持续提高金融服务适应性，增加乡村振兴相关领域贷款投放，充分发挥金融助力乡村振兴作用。', '0', '管理员', '2023-10-12 11:10:32', '', '2023-10-10 20:13:34', 'http://202.194.131.202:9000/village-station/202310111642408975.png', '1', '0', '330', 47, -3, 35);
